#include <bits/stdc++.h>using namespace std;#define ll long long  int main(){    ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);    int t; cin >>t;     while (t--){          int n; cin >>n;          vector <ll> od;          ll sum=0;           for (int i=0; i<n; i++){                 ll a; cin>>a;                 if (a%2!=0){od.push_back(a);continue;}                 sum+=a;              }                    if (od.empty()){cout << 0 <<"\n";continue;}           sort(od.rbegin(), od.rend());           for (int i=0; i<(od.size()+1)/2; i++){              sum+=od[i];          }          cout<<sum<<"\n";              }      return 0;}